define("o9.EKG.validateCustomer", [
  "o9/data/query",
  "o9/data/cellset",
], function () {
  var validateCustomer = function (o9Params) {
    var parsedParams = JSON.parse(o9Params);
    var queryModule = require("o9/data/query");
    var cellsetModule = require("o9/data/cellset");

    Log.Info("o9 PARAMS ---" + parsedParams.City);

    var ExistingIls = `SELECT([o9EKG Customer].[Customer Name].[${parsedParams.Cust}]);`;

    var ExistingCustomer = `Select([o9EKG Customer].[Customer Name].{[${parsedParams.Cust}]});`;
    var temp = cellsetModule.createCellSet(queryModule.select(ExistingIls));

    if (temp.rowCount > 0) {
      RuleOutputToUI = {
        Status: "Error",
        Message: "This Customer already exists. Please enter a new name",
      };
    } else {
      RuleOutputToUI = {
        Status: "Success",
        Message: "Adding New Customer",
      };
    }
    return;
  };

  var addCustomer = function (o9Params) {
    var parsedParams = JSON.parse(o9Params);
    var queryModule = require("o9/data/query");

    const characters =
      "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    let implementerKey = "";
    for (let i = 0; i < 16; i++) {
      implementerKey += characters.charAt(
        Math.floor(Math.random() * characters.length)
      );
    }

    Log.Info("o9 PARAMS ---" + parsedParams.Cust);
    const customerName = parsedParams.Cust;

    try {
      // create a new customer member in the o9EKG Customer dimension
      let createCustomer = `createmember( [o9EKG Customer].[Customer Name] = { , "${customerName}" } );`;

      queryModule.update(createCustomer);

      // populate implementer's key for the new customer
      let populateImplementerKey = `cartesian scope: (&CWV * [o9EKG Customer].[Customer Name].[${customerName}] );
      Measure.[o9EKG Implementer Key] = "${implementerKey}";
      end scope;`;

      queryModule.update(populateImplementerKey);

      RuleOutputToUI = {
        Status: "Success",
        Message: `Added Customer: ${customerName}`,
      };
    } catch (error) {
      RuleOutputToUI = {
        Status: "Error",
        Message: `Encountered error while adding Customer:${error}`,
      };
    } finally {
      return;
    }
  };

  return {
    validateCustomer: validateCustomer,
    addCustomer: addCustomer,
  };
});

