define('o9.EKG.generateDefaultPipeline', ['o9/data/query', 'o9/data/cellset'], function () {
    var generateDefaultPipeline = function (o9Params) {

        const queryModule = require('o9/data/query');
        const cellsetModule = require('o9/data/cellset');
        const parsedParams = JSON.parse(o9Params);

        Log.Info('O9 PARAMS ---' + o9Params);

        const project = parsedParams.Project;
        const tableNames = parsedParams.TableName;

        let batchType = "Default";

        const createDefaultBatchType = `createmember( [o9EKG Batch Type].[Batch Type] = { , "Default" } );`;

        Log.Info(createDefaultBatchType)
        queryModule.update(createDefaultBatchType)

        const createDefaultBatchType2 = `cartesian scope: (&CWV * [o9EKG Customer].[Project Name].[${project}] * [o9EKG Batch Type].[Batch Type].[Default]);
        Measure.[o9EKG Batch Description] = "Default Batch Type";
        end scope;`;


        Log.Info(createDefaultBatchType2)
        queryModule.update(createDefaultBatchType2)


        tableNames.map((tableName) => {
            const tableGroupNameQuery = `Select ([o9EKG Table].[Table Name].[${tableName}]* [o9EKG Table].[Table Type]);`;
            let tableGroupQueryResult = cellsetModule.createCellSet(queryModule.select(tableGroupNameQuery))

            Log.Info(tableGroupNameQuery)

            let tableGroupName = tableGroupQueryResult.row(0).cell(tableGroupQueryResult.memberColumnIndex('o9EKG Table', 'Table Type')).Name

            let isSampleData = false;
            if (tableGroupName === "Sample Data") {
                isSampleData = true;
            }

            if (tableGroupName === "IN") {
                tableGroupName = "IN";
            }
            else if (tableGroupName === "o9 Data Model") {
                tableGroupName = "ODM";
            }
            else if (tableGroupName === "LS_Upload") {
                tableGroupName = "LSU";
            }
            else if (tableGroupName === "Sample Data") {
                tableGroupName = "IN"
            }
            else {
                return;
            }


            let pipelineName = tableGroupName + '_' + tableName;
            if (isSampleData) {
                pipelineName = "Sample Data_" + tableName;
            }

            const createPipelineMemberQuery = `createmember( [o9EKG Pipeline].[Pipeline Name] = { , "${pipelineName}" } );`;
            const populatePipelineAssociationQuery = `cartesian scope: ( &CWV * [o9EKG Pipeline].[Pipeline Name].[${pipelineName}] * [o9EKG Table].[Table Name].[${tableName}]* [o9EKG Batch Type].[Batch Type].[${batchType}]* [o9EKG Customer].[Project Name].[${project}] ) ;
                Measure.[o9EKG Table Order] = 1;
                Measure.[o9EKG Pipeline Folder Group] = "EKG_${tableGroupName}" ;
                end scope;`;

            Log.Info(createPipelineMemberQuery)
            queryModule.update(createPipelineMemberQuery)

            Log.Info(populatePipelineAssociationQuery)
            queryModule.update(populatePipelineAssociationQuery)
        })

        return "";
    }

    return {
        generateDefaultPipeline: generateDefaultPipeline
    };
})

