define('o9.ekgconfig.batchupdate', ['o9/data/query', 'o9/data/cellset'], function() {
    var updatebatch = function(o9Params) {

        var queryModule = require('o9/data/query');
        var cellsetModule = require('o9/data/cellset');
        var parsedParams = JSON.parse(o9Params);
        Log.Info('o9 params ---' + o9Params);

        var project = parsedParams.Project;
        var pipeline = parsedParams.Pipeline;
        var foldergroup = parsedParams.FolderGroup;
        var oldBatch = parsedParams.SelectedBatchType;
        var newBatch = parsedParams.UpdateBatch;
        var tableNames = parsedParams.TableName;

        const formattedTables = tableNames.map(table => `[${table}]`).join(',');

        let NullifyFolderGroupfunction = `scope: ( &CWV * [o9EKG Pipeline].[Pipeline Name].[${pipeline}]  * [o9EKG Customer].[Project Name].[${project}]*[o9EKG Batch Type].[Batch Type].[${oldBatch}]);
                            Measure.[o9EKG Pipeline Folder Group] = null ;
                            end scope;`;
                            
        Log.Info('nullify folder group '+NullifyFolderGroupfunction);
        queryModule.update(NullifyFolderGroupfunction);
                                       
                            
        let NullifyTableOrderfunction = `scope: ( &CWV * [o9EKG Pipeline].[Pipeline Name].[${pipeline}] * [o9EKG Customer].[Project Name].[${project}]*[o9EKG Batch Type].[Batch Type].[${oldBatch}]*[o9EKG Table].[Table Name]);
                            Measure.[o9EKG Table Order] = null;
                            end scope;`;

        Log.Info('nullify folder group '+NullifyTableOrderfunction);
        queryModule.update(NullifyTableOrderfunction);
      

        //cartesian scope= create member 

        let createbatchTypeMemberQuery = `createmember( [o9EKG Batch Type].[Batch Type] = { , "${newBatch}" } );`;
        let populateQuery = `cartesian scope: ( &CWV * [o9EKG Pipeline].[Pipeline Name].[${pipeline}] * [o9EKG Table].[Table Name].filter(#.Name in {${formattedTables}})* [o9EKG Batch Type].[Batch Type].[${newBatch}]* [o9EKG Customer].[Project Name].[${project}] ) ;
                Measure.[o9EKG Table Order] =  "<not defined>";
                Measure.[o9EKG Pipeline Folder Group] = "${foldergroup}" ;
                end scope;`;

       Log.Info("createbatchTypeMemberQuery "+createbatchTypeMemberQuery);
            queryModule.update(createbatchTypeMemberQuery);

        Log.Info("populate final query "+populateQuery);
            queryModule.update(populateQuery);


        //delete member 
        let deleteEarlierIntersection = `DELETE DATA for MODEL [o9EKG Pipeline Properties],[o9EKG Pipeline Table Association] Where { &CWV , [o9EKG Customer].[Project Name].[${project}], [o9EKG Batch Type].[Batch Type].[${oldBatch}],[o9EKG Pipeline].[Pipeline Name].[${pipeline}] ,[o9EKG Table].[Table Name]};`;
        queryModule.update(deleteEarlierIntersection);
       

        
    };




    return {
        updatebatch : updatebatch       
    };
});

