define('o9.EKG.validateCentralTenant', ['o9/data/query', 'o9/data/cellset'], function () {
    var validateCentralTenant = function (o9Params) {

        const queryModule = require('o9/data/query');
        const cellsetModule = require('o9/data/cellset');
        const parsedParams = JSON.parse(o9Params);

        Log.Info('O9 PARAMS ---' + o9Params);

        const project = parsedParams.Project;
        const selectedTenants = parsedParams.SelectedTenants;

        selectedTenants.map((tenant) => {
            const selectQuery = `Select ([o9EKG Tenant].[Tenant ID].[${tenant}] * {Measure.[o9EKG Driver Tenant]}) where {&CWV, [o9EKG Customer].[Project Name].[${project}], Measure.[o9EKG Driver Tenant] == true};`;

            const centralTenantCellSet = cellsetModule.createCellSet(queryModule.select(selectQuery));

            if (centralTenantCellSet.rowCount > 0) {
                RuleOutputToUI = { Status: 'Error', Message: 'One of the selected profiles is a central tenant. Please choose another central tenant before removing these.' };
                return;
            }

        });

        return "";
    }

    return {
        validateCentralTenant: validateCentralTenant
    };
})

