define('o9.referencesupplyplanning.NetChangeDeleteData', ['o9/data/query', 'o9/data/cellset'], function () {
    var NetChangeFlag = function(o9Params){
	Log.Info("Started Java Script o9.referencesupplyplanning.NetChangeDeleteData");
	var queryModule = require('o9/data/query');
	var cellsetModule = require('o9/data/cellset');
	var ParsedParams = JSON.parse(o9Params);
	Log.Info('Passed Fields'+ JSON.stringify(ParsedParams));
	var Scenario = ParsedParams.Version;
	var getNCMeasureGroups = 'Select ([Version].[Version Name].['+Scenario+'] * [Out Table Structure].[Table Name] * [Out Files].[Serial No]) on row, ({Measure.[Output Category Validated], Measure.[Output Measure Group Validated], Measure.[Output NC Enabled Validated]}) on column;';
	var NCMG = queryModule.select(getNCMeasureGroups);
	var MeasureGroups = cellsetModule.createCellSet(NCMG);
	var string1;
	var query = "";
	for (var i = 0; i < MeasureGroups.rowCount ; i++){
		var NCFlag = MeasureGroups.row(i).cell(MeasureGroups.measureColumnIndex('Output NC Enabled Validated'));
		var MG = MeasureGroups.row(i).cell(MeasureGroups.measureColumnIndex('Output Measure Group Validated'));
		var category = MeasureGroups.row(i).cell(MeasureGroups.measureColumnIndex('Output Category Validated'));
		//Log.Info(NCFlag);
		//Log.Info(MG);
		if (NCFlag === "No"){
		if (category === "Fact"){
		string1 = 'TRUNCATE DATA FOR MODEL ['+MG+'] with Versionname ['+Scenario+'];\n';
		query = query.concat(string1);
		Log.Info(query);
		queryModule.update(query);
		}
		}	
	}
};   
	
return {
	NetChangeFlag:NetChangeFlag
	};
});

